import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

const API_BASE_URL = 'http://localhost:5000/api';

const Home = () => {
  return <h1>Welcome to Advisor Client!</h1>;
};

const AdvisorsList = ({ advisors }) => {
  return (
    <>
      <h2>Advisors List</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>SIN</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Health Status</th>
          </tr>
        </thead>
        <tbody>
          {advisors.map((advisor) => (
            <tr key={advisor.id}>
              <td>{advisor.name}</td>
              <td>{advisor.sin}</td>
              <td>{advisor.address}</td>
              <td>{advisor.phone}</td>
              <td>{advisor.healthStatus}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

const App = () => {
  const [advisors, setAdvisors] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    sin: '',
    address: '',
    phone: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_BASE_URL}/advisors`, formData);
      // Clear form fields after successful submission
      setFormData({
        name: '',
        sin: '',
        address: '',
        phone: '',
      });
      // Refresh the advisors list
      fetchAdvisors();
    } catch (error) {
      console.error('Error adding advisor:', error);
    }
  };

  const fetchAdvisors = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/advisors`);
      setAdvisors(response.data);
    } catch (error) {
      console.error('Error fetching advisors:', error);
    }
  };

  useEffect(() => {
    fetchAdvisors();
  }, []);

  return (
    <Router>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">
            Home
          </Link>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link" to="/advisors">
                  Advisors
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>

      <Routes>
        <Route path="/" element={<Home />} />
        <Route
          path="/advisors"
          element={<AdvisorsList advisors={advisors} />}
        />
      </Routes>

      <div className="container mt-4">
        <h2>Add New Advisor</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="name" className="form-label">
              Name
            </label>
            <input
              type="text"
              className="form-control"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              maxLength="255"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="sin" className="form-label">
              SIN
            </label>
            <input
              type="text"
              className="form-control"
              id="sin"
              name="sin"
              value={formData.sin}
              onChange={handleChange}
              required
              minLength="9"
              maxLength="9"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="address" className="form-label">
              Address
            </label>
            <input
              type="text"
              className="form-control"
              id="address"
              name="address"
              value={formData.address}
              onChange={handleChange}
              maxLength="255"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="phone" className="form-label">
              Phone
            </label>
            <input
              type="text"
              className="form-control"
              id="phone"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              minLength="8"
              maxLength="8"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Add Advisor
          </button>
        </form>
      </div>
    </Router>
  );
};

export default App;
